.order-wizard-paymentmethod-creditcard-module {
	.creditcard-edit {
		padding-top: 0;
		padding-bottom: 0;

		border-top: none;

		.creditcard-edit-form {
		  clear: both;
		}
		@media (min-width: $screen-sm-min) {
			padding-left: 0;
		}
	}

	.creditcard-new-card, .paymentinstrument-creditcard-new-card {
		height: 210px;
	}

	.creditcard-new-card-title, .paymentinstrument-creditcard-new-card-title {
		margin-top: 50px;
	}
}

.order-wizard-paymentmethod-creditcard-module-learn-more {
	margin: $sc-margin-lv3 0 0;
	color: $sc-color-theme-700;
	font-size: $sc-font-size-s;
	width: 100%;
	display: inline-block;
}

.order-wizard-paymentmethod-creditcard-module-learn-more-icon {
	@extend .icon-locked;
}

.order-wizard-paymentmethod-creditcard-module-learn-more-link {
	cursor: pointer;
}
.order-wizard-paymentmethod-creditcard-module-selected-card {
	@extend .col-md-6;
	@extend .info-card;
	margin-bottom: 0;
	.creditcard {
		margin-bottom: 15px;
		width: 100%;
		padding: 0;
	}
	.creditcard-content {
		border: 0;
		padding: 0;
	  height: auto;
	  min-height: inherit;
	}
}
.order-wizard-paymentmethod-creditcard-module-actions {
	clear:both;
}
.order-wizard-paymentmethod-creditcard-module-add-new-credit-card-button {
	margin: $sc-margin-lv3 0;
	@extend .button-secondary;
	@extend .button-medium;
}

.order-wizard-paymentmethod-creditcard-module-title{
	margin-bottom: $sc-margin-lv2;
}

.order-wizard-paymentmethod-creditcard-module-edit-card{
	@extend .button-tertiary;
	@extend .button-small;
	margin-right:$sc-margin-lv4;
}

.order-wizard-paymentmethod-creditcard-module-change-card{
	@extend .button-tertiary;
	@extend .button-small;
	margin-top:$sc-margin-lv3;
	@media (min-width: $screen-md-min) {
		margin-top: 0;
	}
}

.order-wizard-paymentmethod-creditcard-module-list-placeholder{
	@extend .creditcard-module-list-placeholder;
}

.order-wizard-paymentmethod-external-module-container {
    @extend .address-card-container;
    cursor: pointer;
    border-bottom: 0px;
    padding: $sc-padding-lv3;
}

.order-wizard-paymentmethod-external-module-details {
    @extend .address-card-container;
}

.order-wizard-paymentmethod-external-module-container-selected {
	background-color: $sc-neutral-shade-0;
}

.order-wizard-paymentmethod-external-module-radio {
	margin-bottom: 0;
}

.order-wizard-paymentmethod-external-module-details-container {
	padding: $sc-padding-lv2 $sc-padding-lv3;
	height: 140px;
    line-height: 120px;
    text-align: center;
}

.order-wizard-paymentmethod-external-module-details-container-name {
	margin-bottom: $sc-padding-lv2;
}

.order-wizard-paymentmethod-external-module-details-container-image {
	display: inline-block;
    margin: 0 auto;
    height: auto;
    width: auto;
    max-width: 100%;
    max-height: 100%;
    vertical-align: middle;
}

.order-wizard-paymentmethod-giftcertificates-module-record-row{
	@extend .recordviews-row;
}

.order-wizard-paymentmethod-giftcertificates-module-record-gift-certificates{
	@extend .giftcertificate-number;
	@extend .recordviews-generic;
	vertical-align:middle;
	.order-wizard-paymentmethod-giftcertificates-module-record-value {
		display: inline-block;
	}
}

.order-wizard-paymentmethod-giftcertificates-module-record-amount-applied{
	@extend .recordviews-generic;
	vertical-align:middle;
}

.order-wizard-paymentmethod-giftcertificates-module-record-remaining-balance{
	@extend .recordviews-generic;
	vertical-align:middle;
}

.order-wizard-paymentmethod-giftcertificates-module-record-actions{
	@extend .giftcertificate-action;
	padding-bottom: $sc-padding-lv3;
}

.order-wizard-paymentmethod-giftcertificates-module-record-actions-button{
	@extend .giftcertificate-action-remove;
}

.order-wizard-paymentmethod-giftcertificates-module-record-label{
	@extend .recordviews-label;
	@media (min-width: $screen-md-min){
		display: none;
	}
}

.order-wizard-paymentmethod-giftcertificates-module {
	margin-bottom: $sc-margin-lv3;
}



.order-wizard-paymentmethod-giftcertificates-module-expander-head-toggle {
	@extend .accordion-head-toggle-secondary;
}

.order-wizard-paymentmethod-giftcertificates-module-info-icon {
	@extend .sc-tooltip;
}

.order-wizard-paymentmethod-giftcertificates-module-expander-icon {
	@extend .accordion-toggle-icon-secondary;
}

.order-wizard-paymentmethod-giftcertificates-module-form {
	@extend .accordion-body-alt;
}

.order-wizard-paymentmethod-giftcertificates-module-form-expander-container {
	@extend .accordion-container;
}

.order-wizard-paymentmethod-giftcertificates-module-form-input-container {
	@extend .col-xs-6;
	float: left;
	padding: 0;
}



.order-wizard-paymentmethod-giftcertificates-module-form-submit-container {
	@extend .col-xs-6;
	float: right;
}

.order-wizard-paymentmethod-giftcertificates-module-form-submit {
	@extend .button-secondary;
	@extend .button-medium;
	margin-left: $sc-margin-lv3;
}

.order-wizard-paymentmethod-giftcertificates-module-table {
	@extend .table-flex-record-actionable-navigable;
	margin-top: $sc-margin-lv2;
}

.order-wizard-paymentmethod-giftcertificates-module-table-header {
	@extend .table-flex-head;
}

.order-wizard-paymentmethod-giftcertificates-module-table-body {
	@extend .table-row-with-border;
}

.giftcertificate-number {
	@extend .table-flex-td-first;
}

.giftcertificate-action {
	@extend .table-flex-td-last;
}

.giftcertificate-action-remove {
	@extend .button-tertiary;
	@extend .button-small;
}



.order-wizard-paymentmethod-giftcertificates-module-step-header{
	@extend .order-wizard-step-header !optional;
}

.order-wizard-paymentmethod-invoice-module-row {
	@extend .clearfix;
	width: 100%;
	margin-bottom: $sc-margin-lv3;
}

.order-wizard-paymentmethod-invoice-module-terms {
	float: left;
	width: 50%;
}

.order-wizard-paymentmethod-invoice-module-terms-label {
	@extend .sc-value;
	font-size: $sc-font-size-m;
	font-weight: $sc-font-weight-semibold;
}

.order-wizard-paymentmethod-invoice-module-terms-value {
	@extend .sc-label;
}

.order-wizard-paymentmethod-invoice-module-balance {
	float: right;
	width: 50%;
}

.order-wizard-paymentmethod-invoice-module-balance-label {
	@extend .sc-value;
	font-size: $sc-font-size-m;
	font-weight: $sc-font-weight-semibold;
}

.order-wizard-paymentmethod-invoice-module-balance-value {
	@extend .sc-label;
	font-size: $sc-font-size-m;
}

.order-wizard-paymentmethod-invoice-module-purchase-order-optional {
	@extend .input-optional;
	font-weight: $sc-font-weight-normal;
}

.order-wizard-paymentmethod-invoice-module-conditions {
	@extend .sc-label;
}

.order-wizard-paymentmethod-others-module-description .global-views-message-info {
    border: 1px solid $sc-color-info-border;
    margin-top: $sc-margin-lv6;
}

.order-wizard-paymentmethod-paypal-module-row{
	@extend .clearfix;
	@extend .col-xs-12;
	@extend .col-sm-4;
	padding: 0;
}

.order-wizard-paymentmethod-paypal-module-container {
    @extend .address-card-container;
    cursor: pointer;
    border-bottom: 0px;
    padding: $sc-padding-lv3;
}

.order-wizard-paymentmethod-paypal-module-details {
    @extend .address-card-container;
}

.order-wizard-paymentmethod-paypal-module-container-selected {
	background-color: $sc-neutral-shade-0;
}

.order-wizard-paymentmethod-paypal-module-radio {
	margin-bottom: 0;
}

.order-wizard-paymentmethod-paypal-module-details-container {
	padding: $sc-padding-lv2 $sc-padding-lv3;
	height: 140px;
    line-height: 120px;
    text-align: center;
}

.order-wizard-paymentmethod-paypal-module-description{
	clear: both;
	@extend .message-info;
	@extend .global-views-message-info;
    margin-top: $sc-margin-lv6;
    width: 100%;
}

.order-wizard-paymentmethod-paypal-module-paypal-logo {
	display: inline-block;
    margin: 0 auto;
    height: auto;
    width: auto;
    max-width: 100%;
    max-height: 100%;
    vertical-align: middle;
}

.order-wizard-paymentmethod-purchasenumber-module {
	@extend .box-column;
	overflow:hidden;
}

.order-wizard-paymentmethod-purchasenumber-module-purchase-order-value {
	@extend .input-large;
}

.order-wizard-paymentmethod-purchasenumber-module-purchase-order-label {
	@extend .sc-value;
	display: block;
	margin-bottom: $sc-margin-lv1;
	font-size: $sc-font-size-m;
	font-weight: $sc-font-weight-semibold;
}

.order-wizard-paymentmethod-purchasenumber-module-row {
	@extend .clearfix;
	width: 100%;
	margin-bottom: $sc-margin-lv3;
}

.order-wizard-paymentmethod-purchasenumber-module-purchase-order-optional {
	@extend .input-optional;
	font-weight: $sc-font-weight-normal;
}

.order-wizard-paymentmethod-purchasenumber-module-title {
	margin-bottom: $sc-margin-lv2;
}

.order-wizard-paymentmethod-selector-module {
	@extend .box-column;

	.creditcard-edit-form {
	  clear: both;
	}
	.creditcard-edit-form-securitycode {
		padding: 0;
	}
}

.order-wizard-paymentmethod-selector-module-header {
	margin-bottom: $sc-margin-lv3;
	color: $sc-color-theme-700;
}

.order-wizard-paymentmethod-selector-module-header-nav {
	margin-bottom: $sc-margin-lv4;
	position:relative;
}

.order-wizard-paymentmethod-selector-module-button-group {
	@extend .pill-button-group;
}

.order-wizard-paymentmethod-selector-module-button {
	@extend .button-medium;
	@extend .pill-button;	
}

.order-wizard-paymentmethod-selector-module-button.selected {
	@extend .pill-button-active;
}

.order-wizard-showpayments-module-details{
	@extend .box-column;
}
.order-wizard-showpayments-module-details-body {
	@extend .row;
	h3 {
		margin-bottom: $sc-margin-lv3;
	}
}
.order-wizard-showpayments-module-billing-address{
	@extend .col-xs-12;
	@extend .col-sm-6;
	margin-bottom: $sc-margin-lv3;

  @media (min-width: $screen-sm-min){
  	margin-bottom: 0
  }
}

.order-wizard-showpayments-module-payment-method{
	@extend .col-xs-12;
	@extend .col-sm-6;

	.payment-methods-summary {
		min-height: inherit;
	}
}
.payment-methods-summary {
	@extend .info-card;
}
.order-wizard-showpayments-module-payment-address-edit {
	margin-top: $sc-margin-lv3;
	display: inline-block;
}
.order-wizard-showpayments-module-billing-address-edit {
  margin-bottom: $sc-margin-lv3;
  margin-top: $sc-margin-lv3;
	display: inline-block;
	@media (min-width: $screen-sm-min){
		margin-bottom: 0;
	}
}



.order-wizard-showpayments-module-payment-methods-summary{
	@extend .payment-methods-summary;
}
